-- Đang đổ dữ liệu cho bảng `vhn_camnhanhocvien`
--

INSERT INTO `vhn_camnhanhocvien` (`id`, `tenhocvien`, `nganhnghe`, `nganhnghejp`, `noidung`, `noidungjp`, `image`, `ngayxuatcanh`, `stt`) VALUES
(13, 'Đặng Thị Yến Nhi', 'Điều dưỡng', 'Chưa dịch', 'Thật khó khăn khi phải sống và làm việc trong một môi trường xa lạ, nhưng nếu có được  sự quan tâm và hổ trợ từ nơi khác thì đó sẽ là nguồn động lực quý giá cho bạn vững bước hơn. \r\nTừ những ngày đầu vừa bước chân vào ACM, tôi cảm thấy thật xa lạ và có cảm giác lo sợ. Tuy nhiên, nhờ sự nhiệt tình của các anh chị tư vấn viên cũng như sự ân cần của đội ngũ giảng viên tôi đã cảm thấy tốt hơn. \r\nĐó là sự kiên nhẫn, ân cần dạy từng con chữ. Chia sẽ những lo lắng khi ngày đầu bước chân đến nơi xa lạ, chính những điều đó đã tạo nên sự ấm áp cho tôi. \r\nGiờ đây, tôi đã có công việc phù hợp, cuộc sống cũng không còn bỡ ngỡ như ban đầu chính là nhờ vào ACM. Qua đây tôi xin gởi lời cảm ơn chân thành nhất đến với ACM, nơi đã giúp tôi tự tin và trưởng thành như bây giờ.', 'Thật khó khăn khi phải sống và làm việc trong một môi trường xa lạ, nhưng nếu có được  sự quan tâm và hổ trợ từ nơi khác thì đó sẽ là nguồn động lực quý giá cho bạn vững bước hơn. \r\nTừ những ngày đầu vừa bước chân vào ACM, tôi cảm thấy thật xa lạ và có cảm giác lo sợ. Tuy nhiên, nhờ sự nhiệt tình của các anh chị tư vấn viên cũng như sự ân cần của đội ngũ giảng viên tôi đã cảm thấy tốt hơn. \r\nĐó là sự kiên nhẫn, ân cần dạy từng con chữ. Chia sẽ những lo lắng khi ngày đầu bước chân đến nơi xa lạ, chính những điều đó đã tạo nên sự ấm áp cho tôi. \r\nGiờ đây, tôi đã có công việc phù hợp, cuộc sống cũng không còn bỡ ngỡ như ban đầu chính là nhờ vào ACM. Qua đây tôi xin gởi lời cảm ơn chân thành nhất đến với ACM, nơi đã giúp tôi tự tin và trưởng thành như bây giờ.', 'miraiuploads/h%E1%BB%8Dc%20vien/DANG%20THI%20YEN%20NHI%2004.05.2001-min.jpg', '2020-02-20', 1),
(14, 'Hoài Nam', 'Xây dựng', 'Chưa dịch', 'Chào các bạn, sau 3 năm làm việc tại Nhật Bản thì cuối cùng tôi đã kết thúc hợp đồng và được trở lại quê hương. Trước những niềm vui và cảm giác xúc đó đã thúc đẩy tôi viết những lời cảm nhận ngày hôm nay, chia sẻ vẻ với các bạn về hành trình đi Nhật của tôi. \r\nCó lẽ tôi rất mai mắn khi được biết đến và tham gia vào ngôi nhà chung của ACM. Vì tôi sẽ chẳng thành công được nếu như không có sự dạy bảo của các giảng viên nơi đây. Nhờ sự kiên nhẫn giảng dạy đó mà tôi mới học tập được ngôn ngữ và những kiến thức về ngành nghề mà mình sắp tham gia, cũng như kinh nghiệm sống. Hành trang tôi mang theo trong suốt quá trình làm việc tại Nhật không chỉ là kiến thức mà còn là niềm tin mà mọi người đã gởi gắm. Đó là nguồn động lực giúp tôi vượt qua được những khó khăn. Tôi muốn gởi làm cảm ơn chân thành nhất đến tất cả mọi người, và chúc công ty ngày càng phát triển hơn nữa để có thể luôn luôn đồng hành cùng các lao động Việt', 'Chào các bạn, sau 3 năm làm việc tại Nhật Bản thì cuối cùng tôi đã kết thúc hợp đồng và được trở lại quê hương. Trước những niềm vui và cảm giác xúc đó đã thúc đẩy tôi viết những lời cảm nhận ngày hôm nay, chia sẻ vẻ với các bạn về hành trình đi Nhật của tôi. \r\nCó lẽ tôi rất mai mắn khi được biết đến và tham gia vào ngôi nhà chung của ACM. Vì tôi sẽ chẳng thành công được nếu như không có sự dạy bảo của các giảng viên nơi đây. Nhờ sự kiên nhẫn giảng dạy đó mà tôi mới học tập được ngôn ngữ và những kiến thức về ngành nghề mà mình sắp tham gia, cũng như kinh nghiệm sống. Hành trang tôi mang theo trong suốt quá trình làm việc tại Nhật không chỉ là kiến thức mà còn là niềm tin mà mọi người đã gởi gắm. Đó là nguồn động lực giúp tôi vượt qua được những khó khăn. Tôi muốn gởi làm cảm ơn chân thành nhất đến tất cả mọi người, và chúc công ty ngày càng phát triển hơn nữa để có thể luôn luôn đồng hành cùng các lao động Việt', 'miraiuploads/h%E1%BB%8Dc%20vien/z799041246580_30e0b26071bc047ce1a261cd60ea8342.jpg', '2020-02-20', 1),
(16, 'Nguyễn Văn Cương', 'Hàn', 'Chưa dịch', 'Trong những công ty tôi đã từng làm việc, tôi nhân thấy môi trường làm việc ở đây thật phù hợp với tôi. Mặc dù tôi mới vào làm việc tại Công ty nhưng với môi chuyên nghiệp, mọi người thân thiện, hòa đồng, đoàn kết với nhau và điều quan trọng nữa là toàn thể nhân viên mới trước khi nhiệm vụ đều được Công ty huấn luyện về An toàn – vệ sinh lao động và được trang bị đầy đủ các phương tiện bảo hội lao động. Đồng thời là động lực giúp tôi luôn phấn đấu, học hỏi kinh nghiệm từ các đồng nghiệp để hoàn thành và trưởng thành hơn trong công việcKhông dừng lại ở đó em còn được rèn luyện về tính cách, tinh thần lẫn thể lực, ngày qua ngày mọi thứ cứ tốt dần lên. Giờ đây, khi chuẩn bị bước lên chuyến bay của cuộc đời, em nhận ra sang Nhật làm việc không còn là ước mơ nữa... nó sắp thành hiện thực rồi thầy cô ơi! Tháng tới em sẽ bay đến tỉnh Chiba để làm về gia công thép.', 'Trong những công ty tôi đã từng làm việc, tôi nhân thấy môi trường làm việc ở đây thật phù hợp với tôi. Mặc dù tôi mới vào làm việc tại Công ty nhưng với môi chuyên nghiệp, mọi người thân thiện, hòa đồng, đoàn kết với nhau và điều quan trọng nữa là toàn thể nhân viên mới trước khi nhiệm vụ đều được Công ty huấn luyện về An toàn – vệ sinh lao động và được trang bị đầy đủ các phương tiện bảo hội lao động. Đồng thời là động lực giúp tôi luôn phấn đấu, học hỏi kinh nghiệm từ các đồng nghiệp để hoàn thành và trưởng thành hơn trong công việcKhông dừng lại ở đó em còn được rèn luyện về tính cách, tinh thần lẫn thể lực, ngày qua ngày mọi thứ cứ tốt dần lên. Giờ đây, khi chuẩn bị bước lên chuyến bay của cuộc đời, em nhận ra sang Nhật làm việc không còn là ước mơ nữa... nó sắp thành hiện thực rồi thầy cô ơi! Tháng tới em sẽ bay đến tỉnh Chiba để làm về gia công thép.', 'miraiuploads/Home/user.jpg', '2020-07-02', 1),
(17, 'Vương Hoàng Anh Khoa', 'Xây dựng', 'Xây dựng', 'ACM là một doanh nghiệp vô cùng uy tín và chất lượng, nhờ biết được công ty mà tôi đã có thể tìm ra con đường mới cho bản thân. \r\nTại đây tôi đã được các anh chị tư vấn và hướng dẫn tận tình, có được một môi trường học tập vô cùng tốt, rèn luyện thêm những kỹ năng mới. \r\nCơ sở vật chất của công ty cũng vô cùng hiện đại và đầy đủ, khi đăng kí tham gia chương trình tại đây tôi được sinh hoạt trong kí túc xá của công ty, có cơ hội tiếp xúc với các thiết bị công nghệ thông tin. \r\nGiảng viên rất thân thiện và kinh nghiệm, dạy tận tâm và dễ hiểu. \r\nHiện tại tôi đang chờ ngày để xuất cảnh sang Nhật Bản làm việc. Vì thế, có đôi lời cảm nhận gởi đến công ty, cám ơn tất cả mọi người vì đã giúp đỡ tôi trong thời gian qua.', 'ACM là một doanh nghiệp vô cùng uy tín và chất lượng, nhờ biết được công ty mà tôi đã có thể tìm ra con đường mới cho bản thân. \r\nTại đây tôi đã được các anh chị tư vấn và hướng dẫn tận tình, có được một môi trường học tập vô cùng tốt, rèn luyện thêm những kỹ năng mới. \r\nCơ sở vật chất của công ty cũng vô cùng hiện đại và đầy đủ, khi đăng kí tham gia chương trình tại đây tôi được sinh hoạt trong kí túc xá của công ty, có cơ hội tiếp xúc với các thiết bị công nghệ thông tin. \r\nGiảng viên rất thân thiện và kinh nghiệm, dạy tận tâm và dễ hiểu. \r\nHiện tại tôi đang chờ ngày để xuất cảnh sang Nhật Bản làm việc. Vì thế, có đôi lời cảm nhận gởi đến công ty, cám ơn tất cả mọi người vì đã giúp đỡ tôi trong thời gian qua.', 'miraiuploads/h%E1%BB%8Dc%20vien/e77d3caa14aee4f0bdbf-min.jpg', NULL, 1);

